export default {
  data: function data() {
    return {
      ClientVipDepositVo: {
        clientId: '',
        clientName: '',
        sex: '',
        age: '',
        deposit: '',
        depositState: '',
        phone: ''
      }
    };
  },
  methods: {
    cancel: function cancel() {
      this.$tabs.close();
    },
    init: function init() {
      this.search();
    },
    search: function search() {
      var _this = this;

      var self = this;
      this.http.get({
        url: "/client-vip-deposit-vo/info",
        param: {
          clientId: this.$data.ClientVipDepositVo.clientId
        }
      }).then(function (data) {
        _this.$data.ClientVipDepositVo = data;

        if (data.sex) {
          _this.ClientVipDepositVo.sex = "男";
        } else {
          _this.ClientVipDepositVo.sex = "女";
        }

        console.log(data);
      });
    }
  },
  created: function created() {
    this.$data.ClientVipDepositVo.clientId = this.$route.params.clientId;
    this.init();
    this.search();
  },
  mounted: function mounted() {}
};
