export default {
  data: function data() {
    return {
      buyOrder: {
        buyOrderId: "",
        buyOrderNumber: "",
        clientName: "",
        orderMoney: "",
        orderStatus: "",
        exerciseStart: "",
        exerciseEnd: "",
        exerciseTime: "",
        orderEnd: "",
        shopName: "",
        vipDeleteFlag: ""
      }
    };
  },
  beforeRouteLeave: function beforeRouteLeave(to, from, next) {
    this.cancel();
  },
  methods: {
    cancel: function cancel() {
      this.$tabs.close();
    },
    init: function init() {
      this.search();
    },
    search: function search() {
      var _this = this;

      var self = this;
      this.http.get({
        url: "/buy-order-client-shop-vo/info",
        param: {
          buyOrderId: this.$data.buyOrder.buyOrderId
        }
      }).then(function (data) {
        _this.$data.buyOrder = data;

        if (_this.$data.buyOrder.orderStatus) {
          _this.$data.buyOrder.orderStatus = "已支付";
        } else {
          _this.$data.buyOrder.orderStatus = "待支付";
          _this.$data.buyOrder.orderEnd = null;
        }

        if (_this.$data.buyOrder.vipDeleteFlag) {
          _this.$data.buyOrder.vipDeleteFlag = "是"; //this.$data.buyOrder.orderEnd = null

          _this.$data.buyOrder.orderStatus = "已支付";
        } else {
          _this.$data.buyOrder.vipDeleteFlag = "否";
        }
      });
    }
  },
  created: function created() {
    this.$data.buyOrder.buyOrderId = this.$route.params.buyOrderId;
    this.init();
    this.search();
  }
};
