      this.http.post({
        url: "/shop/info",
        param: this.$data.shop
      }).then(function (data) {
        _this.message.info("新增成功");

        _this.$router.push({
          name: "shop"
        });
      });
    },
    searchMenjin: function searchMenjin() {
      var _this2 = this;

      this.http.get({
        url: "/menjin/list"
      }).then(function (data) {
        for (var jndex in data) {
          _this2.$data.allMenjin.push(data[jndex]);
        }
      });
    },
    cancel: function cancel() {
      this.$tabs.close();
    }
  },
  created: function created() {
    this.searchMenjin();
  },
  beforeRouteLeave: function beforeRouteLeave(to, from, next) {
    this.cancel();
  }
};
