export default {
  data: function data() {
    return {
      shop: {
        shopId: "",
        shopName: "",
        shopAddress: "",
        shopPhone: ""
      },
      menjin: [],
      allMenjin: [],
      menjins: []
    };
  },
  methods: {
    submit: function submit() {
      var _this = this;

      this.$data.shop.menjins = JSON.stringify(this.$data.menjins);
      this.http.put({
        url: "/shop/info",
        param: this.$data.shop
      }).then(function () {
        _this.message.info("修改成功");
      }).then(function () {
        _this.$router.push({
          name: "shop"
        });
      });
    },
    searchMendian: function searchMendian() {
      var _this2 = this;

      this.http.get({
        url: "/shop/info",
        param: {
          shopId: this.$data.shop.shopId
        }
      }).then(function (data) {
        _this2.$data.shop = data.shop;
        _this2.$data.menjin = data.menjin;
        _this2.$data.allMenjin = data.allMenjin;
        console.log(_this2.$data.shop);

        for (var jndex in data.menjin) {
          _this2.$data.menjins.push(data.menjin[jndex].menjinId);
        }
      });
    },
    cancel: function cancel() {
      this.$tabs.close();
    }
  },
  created: function created() {
    this.$data.shop.shopId = this.$route.params.shopId;
    this.searchMendian();
  },
  //导航离开该组件的对应路由时调用
  //可以访问组件'this'
  beforeRouteLeave: function beforeRouteLeave(to, from, next) {
    this.cancel();
  }
};
