export default {
  data: function data() {
    return {
      card: {
        cardName: "",
        periodDate: "",
        cardMoney: ""
      }
    };
  },
  methods: {
    submit: function submit() {
      var _this = this;

      if (this.$data.card.cardName.length === 0) {
        this.message.error("会员卡名必须输入");
        return;
      }

      if (this.$data.card.periodDate.length === 0) {
        this.message.error("会员卡有效天数必须输入");
        return;
      }

      if (this.$data.card.cardMoney.length === 0) {
        this.message.error("会员卡价格必须输入");
        return;
      }

      this.http.post({
        url: "/card/info",
        param: this.$data.card
      }).then(function (data) {
        _this.message.info("新增成功");

        _this.$router.go(-1);

        _this.$tabs.close();
      });
    },
    cancel: function cancel() {
      this.$router.go(-1);
      this.$tabs.close();
    }
  },
  created: function created() {}
};
