export default {
	data() {
		return {
			card: {
				cardName: "",
				periodDate: "",
				cardMoney: "",
			},
		}
	},
	methods: {
		submit: function() {

			if (this.$data.card.cardName.length === 0) {
				this.message.error("会员卡名必须输入");
				return;
			}

			if (this.$data.card.periodDate.length === 0) {
				this.message.error("会员卡有效天数必须输入");
				return;
			}

			if (this.$data.card.cardMoney.length === 0) {
				this.message.error("会员卡价格必须输入");
				return;
			}

			this.http.post({
				url: "/card/info",
				param: this.$data.card
			}).then(data => {
				this.message.info("新增成功");
				this.$router.go(-1);
				this.$tabs.close();
			});
		},
		cancel: function() {
			this.$router.go(-1);
			this.$tabs.close();
		}
	},
	created: function() {},
}
