    export default {
        data() {
            return {
                ClientVipDepositVo: {
					clientId:'',
					clientName:'',
					sex:'',
					age:'',
					deposit:'',
					depositState:'',
					phone:''
                }				
            }
        },
        methods: {		
            cancel: function () {
                this.$tabs.close();
            },
            init: function () {
                this.search();
            },
            search: function () {
                let self = this;
                this.http.get({
                    url: "/client-vip-deposit-vo/info",
                    param: {
                        clientId: this.$data.ClientVipDepositVo.clientId
                    }
                }).then(data => {						
					this.$data.ClientVipDepositVo = data;
					if(data.sex){
						this.ClientVipDepositVo.sex = "男";
					}else{
						this.ClientVipDepositVo.sex = "女";
					}					
					console.log(data)
                });				
            },
        },
        created: function () {
			this.$data.ClientVipDepositVo.clientId = this.$route.params.clientId;			
			this.init();
			this.search();			
        },
        mounted: function () {		

        }
    }
