export default {
	data() {
		return {
			card: {
				cardName: "",
				periodDate: "",
				cardMoney: "",
				cardId: 0,
			},
		}
	},
	methods: {
		cancel: function() {
			this.$router.go(-1);
			this.$tabs.close();
		},
		submit: function() {
			this.http.put({
				url: "/card/info",
				param: this.card,
			}).then(() => {
				this.message.info("修改成功");
				this.$router.go(-1);
				this.$tabs.close();
			});
		},
		init: function() {
			this.search();
		},
		search: function() {
			this.http.get({
				url: "/card/info",
				param: {
					cardId: this.$data.card.cardId
				}
			}).then(data => {
				this.card = data;
			});
		},
	},
	created: function() {},
	mounted: function() {
		this.$data.card.cardId = this.$route.params.cardId;
		this.init();
	}
}
