            }
        },
        methods: {
			roleChange: function(roleId) {

			},
            cancel: function () {
                this.$tabs.close();
            },
            submit: function () {
                let self = this;
				let roles = [];
				for (let index in this.$data.roles) {
					let roleItem = this.$data.roles[index];
					if (roleItem.has) {
						roles.push(roleItem.roleId);
					}
				}
				// self.user.openId = 0;
				self.user.roles = JSON.stringify(roles);
				delete self.user.ct;
				delete self.user.ut;
				delete self.user.cu;
				delete self.user.uu;
				delete self.user.openId;
				console.log(self.user);
                this.http.put({
                    url: "/user/info",
                    param: self.user,
                }).then(() => {
                    self.message.info("修改成功");
                });
            },
            init: function () {
                this.search();
            },
            searchUser: function () {
                let self = this;

                this.http.get({
                    url: "/user/info",
                    param: {
                        userId: self.$data.user.userId
                    }
                }).then(data => {
					self.user = data.user;
					for (let jndex in self.allRoles) {
						let role = self.allRoles[jndex];
						role.has = false;
						for (let index in data.roles) {
							let roleItem = data.roles[index];
							if (roleItem.roleId == role.roleId) {
								role.has = true;
								this.roles.push(role);
							}
						}
					}

                });
            },

            search: function () {
                let self = this;
				// console.log("hahahahah");
                this.http.get({
                    url: "/role/all",
                }).then(data => {
                    self.$data.allRoles = data;
					console.log(self.allRoles);
                }).then(() => {
                    self.searchUser();
                });
            }

        },

        created: function () {

        },
        mounted: function () {
			this.$data.user.userId = this.$route.params.userId;
            this.init();
        }
