export default {
	data() {
		return {
			shopMenjinVo: {

			},
			shop: []

		}
	},

	methods: {
		cancel: function() {
			this.$tabs.close();
		},
		find: function() {
			this.http.get({
				url: "/shop/page",

			}).then(data => {

				this.$data.shop = data.records;
			});
		},
		submit: function() {
			let self = this;
			this.http.put({
				url: "/shop-menjin-vo/info",
				param: self.shopMenjinVo,
			}).then(() => {
				self.message.info("修改成功");
				this.$tabs.close();
			});
		},
		init: function() {
			this.searchAll();
		},

		searchAll: function() {
			let self = this;
			this.http.get({
				url: "/shop-menjin-vo/info",
				param: {
					menjinId: this.$data.shopMenjinVo.menjinId
				}
			}).then(data => {
				this.$data.shopMenjinVo = data;
			});
		},

	},
	created: function() {
		this.$data.shopMenjinVo.menjinId = this.$route.params.menjinId;
		this.searchAll();
		this.find();
		this.init();
	},
	beforeRouteLeave (to, from, next) {
		this.cancel();
	    }
}
